      newLabel.backgroundColor = [UIColor redColor];        newLabel.textAlignment = NSTextAlignmentCenter;        newLabel.font = [UIFont systemFontOfSize:FONT_SIZE_7];        newLabel.textColor = [UIColor whiteColor];        [self addSubview:newLabel];        [newLabel release];    } else if (type == PTDot) {        UIView *iconView = [[UIView alloc] initWithFrame:CGRectMake(self.frame.size.width / 2 + 16, 1, 8,8)];        iconView.layer.masksToBounds = YES;        iconView.layer.cornerRadius = 4;        iconView.backgroundColor = [TPDialerResourceManager getColorForStyle:@"tp_red_1_color"];        [self addSubview:iconView];        [iconView release];    }}@end